#include<iostream>
using namespace std;
char nums[] = "";

long long ans, zero_cnt, now = 1; 
int main () {
    for (int i = 0; i < 1000; i++) {
        if (i < 13) {
            now *= nums[i] - '0';
        } else {
            if (nums[i] == '0') {
                zero_cnt++;
            } else {
                now *= nums[i] - '0';
            }
            if (nums[i - 13] == '0') {
                zero_cnt--;
            } else {
                now /= nums[i - 13] - '0';
            }
        }
        if (zero_cnt == 0) ans = max(ans, now);
    } 
    cout << ans << endl;

    return 0;
}
